import pro1 from "../assets/image.jpg";
import pro2 from "../assets/BI.jpg";
import pro3 from "../assets/mobile.jpg";
import pro4 from "../assets/health.jpg";


const ProjectCardData = [
  {
    imgsrc: pro1,
    title: "Application de Gestion des Notes",
    text:
      "Ce projet a donné naissance à une application de gestion des notes adaptée aux besoins spécifiques. Grâce à Java et une base de données MySQL, cette solution simplifie la gestion académique. L'utilisation de Bootstrap améliore la lisibilité. L'application permet la création, lecture, mise à jour et suppression des notes pour les étudiants, enseignants et administrateurs. Ses fonctionnalités efficaces bénéficient aux administrateurs.",
    view: "https://github.com/salahcurvasud/projects.git",
  },
  {
    imgsrc: pro2,
    title: "Visualisation Formula One with Tableau",
    text:
      "À travers cette visualisation, j ai pour objectif de rendre l'exploration des données en Formule 1 accessible à tous les utilisateurs, qu ils soient novices ou experts. En utilisant Tableau Public et Python pour le nettoyage des données, je facilite la découverte et la compréhension des informations. Que ce soit pour obtenir des insights objectifs ou formuler des évaluations subjectives, cette approche vise à offrir une expérience enrichissante aux passionnés de Formule 1.",
  },
  {
    imgsrc: pro3,
    title: "Application Mobile HelpCasaMobile",
    text:
      "HelpCasaMobile révolutionne la gestion immobilière de -SeLoger-  avec convivialité. Intègre Android Studio, Java, XML, JavaScript, et SQLite. Gère clients, annonces, commissions, et facturation, avec flexibilité. Interface intuitive, clôture annonces, paiements commissions intégrés. ",
  },
  {
    imgsrc: pro4,
    title: "Application Web HealthCare pour les Cabinets Médicaux",
    text:
      "À l aide d'HTML, CSS, React et MySQL, notre solution permet aux patients de prendre des rendez-vous en ligne, recevoir des rappels automatiques, annuler facilement leurs rendez-vous, et consulter leur historique de consultations, assurant une expérience utilisateur efficace et complète.",
  },
];

export default ProjectCardData;
